import React, { useEffect, useState } from "react";

function TimeHistory() {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("timeHistory") || "[]");
    setHistory(data);
  }, []);

  const handleRemove = (id) => {
    const newHistory = history.filter((item) => item.id !== id);
    setHistory(newHistory);
    localStorage.setItem("timeHistory", JSON.stringify(newHistory));
  };

  // แบ่งข้อมูลเป็น 2 คอลัมน์
  const col1 = history.filter((_, idx) => idx % 2 === 0);
  const col2 = history.filter((_, idx) => idx % 2 === 1);

  return (
    <div className="min-h-screen bg-blue-50 flex justify-center py-10 px-2">
      <div className="w-full max-w-5xl flex flex-col md:flex-row gap-6">
        {/* Column 1 */}
        <div className="flex-1 flex flex-col gap-6">
          {col1.map((item) => (
            <div
              key={item.id}
              className="bg-white border border-blue-200 rounded-lg shadow-sm p-6 flex flex-col gap-2"
            >
              <div className="text-gray-600 flex items-center gap-2">
                <span className="text-lg">🕒</span>
                <span>วันที่ตั้งเวลา: {item.date}</span>
              </div>
              <div className="text-gray-600 flex items-center gap-2">
                <span className="text-lg">⏱</span>
                <span>ระยะเวลาแสดงผล: {item.duration}</span>
              </div>
              <div className="text-gray-600 flex items-center gap-2">
                <span className="text-lg">💵</span>
                <span>ราคา: {item.price}</span>
              </div>
              <button
                className="mt-2 self-end bg-red-500 hover:bg-red-600 text-white px-4 py-1 rounded font-semibold"
                onClick={() => handleRemove(item.id)}
              >
                REMOVE
              </button>
            </div>
          ))}
        </div>
        {/* Column 2 */}
        <div className="flex-1 flex flex-col gap-6">
          {col2.map((item) => (
            <div
              key={item.id}
              className="bg-white border border-blue-200 rounded-lg shadow-sm p-6 flex flex-col gap-2"
            >
              <div className="text-gray-600 flex items-center gap-2">
                <span className="text-lg">🕒</span>
                <span>วันที่ตั้งเวลา: {item.date}</span>
              </div>
              <div className="text-gray-600 flex items-center gap-2">
                <span className="text-lg">⏱</span>
                <span>ระยะเวลาแสดงผล: {item.duration}</span>
              </div>
              <div className="text-gray-600 flex items-center gap-2">
                <span className="text-lg">💵</span>
                <span>ราคา: {item.price}</span>
              </div>
              <button
                className="mt-2 self-end bg-red-500 hover:bg-red-600 text-white px-4 py-1 rounded font-semibold"
                onClick={() => handleRemove(item.id)}
              >
                REMOVE
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default TimeHistory;
