import React, { useEffect, useState } from "react";

function TimeHistory() {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("timeHistory") || "[]");
    setHistory(data);
  }, []);

  const handleRemove = (id) => {
    const newHistory = history.filter((item) => item.id !== id);
    setHistory(newHistory);
    localStorage.setItem("timeHistory", JSON.stringify(newHistory));
  };

  // ‡πÅ‡∏¢‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ï‡∏≤‡∏°‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó mode
  const textHistory = history.filter((item) => item.mode === "text");
  const imageHistory = history.filter((item) => item.mode === "image");

  return (
    <div
      className="min-h-screen w-full flex flex-col items-center"
      style={{
        background: "linear-gradient(135deg, #e6ecf4 0%, #f5fafd 100%)",
        minHeight: "100vh",
        width: "100vw",
        position: "relative",
        overflowX: "hidden",
        paddingTop: "110px", // ‡πÉ‡∏´‡πâ main ‡πÑ‡∏°‡πà‡πÇ‡∏î‡∏ô header ‡∏ó‡∏±‡∏ö
      }}
    >
      {/* Header */}
      <header
        className="w-full fixed top-0 left-0 z-50 flex items-center justify-center bg-white shadow"
        style={{
          height: "110px",
          padding: "1.2rem 2.5rem",
          boxSizing: "border-box",
        }}
      >
        <div
          style={{
            fontSize: "2.5rem",
            color: "#1a237e",
            fontWeight: "bold",
            textAlign: "center",
            flex: 1,
            letterSpacing: "1px",
          }}
        >
          ‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥‡∏Å‡∏≤‡∏£‡∏ï‡∏±‡πâ‡∏á‡πÄ‡∏ß‡∏•‡∏≤
        </div>
      </header>

      {/* Main content */}
      <main className="w-full flex justify-center">
        <div className="w-full max-w-5xl flex flex-col md:flex-row gap-8 justify-center items-start">
          {/* ‡∏Å‡∏£‡∏≠‡∏ö‡∏ã‡πâ‡∏≤‡∏¢ (‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°) */}
          <div className="flex-1 bg-white rounded-2xl shadow-xl border border-blue-200 flex flex-col min-w-[280px] max-w-xl">
            <div className="text-xl font-bold text-blue-900 text-center py-4 border-b border-blue-100 rounded-t-2xl">
              ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏Å‡∏≤‡∏£‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤ <span className="text-pink-600">‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°</span>
            </div>
            {textHistory.length === 0 ? (
              <div className="text-center text-gray-400 py-12 text-lg">‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥‡∏Å‡∏≤‡∏£‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°</div>
            ) : (
              textHistory.map((item, idx) => (
                <div
                  key={item.id}
                  className={`flex flex-col gap-2 px-8 py-6 ${
                    idx !== textHistory.length - 1 ? "border-b border-blue-100" : ""
                  }`}
                >
                  <div className="flex flex-col gap-2">
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">üïí</span>
                      <span className="font-medium">‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏ï‡∏±‡πâ‡∏á‡πÄ‡∏ß‡∏•‡∏≤:</span>
                      <span>{item.date}</span>
                    </div>
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">‚è±</span>
                      <span className="font-medium">‡∏£‡∏∞‡∏¢‡∏∞‡πÄ‡∏ß‡∏•‡∏≤‡πÅ‡∏™‡∏î‡∏á‡∏ú‡∏•:</span>
                      <span>{item.duration}</span>
                    </div>
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">üíµ</span>
                      <span className="font-medium">‡∏£‡∏≤‡∏Ñ‡∏≤:</span>
                      <span>{item.price}</span>
                    </div>
                  </div>
                  <button
                    className="self-end bg-red-500 hover:bg-red-600 text-white px-5 py-1.5 rounded font-semibold shadow-sm transition mt-2"
                    onClick={() => handleRemove(item.id)}
                  >
                    REMOVE
                  </button>
                </div>
              ))
            )}
          </div>
          {/* ‡∏Å‡∏£‡∏≠‡∏ö‡∏Ç‡∏ß‡∏≤ (‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û) */}
          <div className="flex-1 bg-white rounded-2xl shadow-xl border border-blue-200 flex flex-col min-w-[280px] max-w-xl">
            <div className="text-xl font-bold text-blue-900 text-center py-4 border-b border-blue-100 rounded-t-2xl">
              ‡∏£‡∏≤‡∏¢‡∏•‡∏∞‡πÄ‡∏≠‡∏µ‡∏¢‡∏î‡∏Å‡∏≤‡∏£‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤ <span className="text-indigo-600">‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û</span>
            </div>
            {imageHistory.length === 0 ? (
              <div className="text-center text-gray-400 py-12 text-lg">‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥‡∏Å‡∏≤‡∏£‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤‡∏£‡∏π‡∏õ‡∏†‡∏≤‡∏û</div>
            ) : (
              imageHistory.map((item, idx) => (
                <div
                  key={item.id}
                  className={`flex flex-col gap-2 px-8 py-6 ${
                    idx !== imageHistory.length - 1 ? "border-b border-blue-100" : ""
                  }`}
                >
                  <div className="flex flex-col gap-2">
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">üïí</span>
                      <span className="font-medium">‡∏ß‡∏±‡∏ô‡∏ó‡∏µ‡πà‡∏ï‡∏±‡πâ‡∏á‡πÄ‡∏ß‡∏•‡∏≤:</span>
                      <span>{item.date}</span>
                    </div>
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">‚è±</span>
                      <span className="font-medium">‡∏£‡∏∞‡∏¢‡∏∞‡πÄ‡∏ß‡∏•‡∏≤‡πÅ‡∏™‡∏î‡∏á‡∏ú‡∏•:</span>
                      <span>{item.duration}</span>
                    </div>
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">üíµ</span>
                      <span className="font-medium">‡∏£‡∏≤‡∏Ñ‡∏≤:</span>
                      <span>{item.price}</span>
                    </div>
                  </div>
                  <button
                    className="self-end bg-red-500 hover:bg-red-600 text-white px-5 py-1.5 rounded font-semibold shadow-sm transition mt-2"
                    onClick={() => handleRemove(item.id)}
                  >
                    REMOVE
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default TimeHistory;
