import React, { useEffect, useState } from "react";

function TimeHistory() {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("timeHistory") || "[]");
    setHistory(data);
  }, []);

  const handleRemove = (id) => {
    const newHistory = history.filter((item) => item.id !== id);
    setHistory(newHistory);
    localStorage.setItem("timeHistory", JSON.stringify(newHistory));
  };

  // แยกข้อมูลตามประเภท mode
  const textHistory = history.filter((item) => item.mode === "text");
  const imageHistory = history.filter((item) => item.mode === "image");

  return (
    <div className="min-h-screen bg-blue-50 flex flex-col items-center">
      {/* Header */}
      <header
        className="main-header"
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          maxWidth: "100vw",
          zIndex: 100,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: "1.2rem 2.5rem",
          boxSizing: "border-box",
          background: "#fff",
          boxShadow: "0 2px 12px rgba(30, 41, 59, 0.08)",
        }}
      >
        <div
          style={{
            fontSize: "2.5rem",
            color: "#1a237e",
            fontWeight: "bold",
            textAlign: "center",
            flex: 1,
            letterSpacing: "1px",
          }}
        >
          ประวัติการตั้งเวลา
        </div>
      </header>

      {/* Main content */}
      <main className="w-full flex justify-center" style={{ marginTop: "110px" }}>
        <div className="w-full max-w-5xl flex flex-col md:flex-row gap-8 justify-center items-stretch">
          {/* กล่องฝั่งซ้าย (ข้อความ) */}
          <div className="flex-1 bg-white rounded-2xl shadow-xl border border-blue-200 flex flex-col min-w-[320px]">
            <div className="text-xl font-bold text-blue-900 text-center py-4 border-b border-blue-100 rounded-t-2xl">
              รายละเอียดการตั้งค่า <span className="text-pink-600">ข้อความ</span>
            </div>
            {textHistory.length === 0 ? (
              <div className="text-center text-gray-400 py-12 text-lg">ไม่มีประวัติการตั้งค่าข้อความ</div>
            ) : (
              textHistory.map((item, idx) => (
                <div
                  key={item.id}
                  className={`flex flex-col gap-2 px-8 py-6 ${
                    idx !== textHistory.length - 1 ? "border-b border-blue-100" : ""
                  }`}
                >
                  <div className="flex flex-col gap-2">
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">🕒</span>
                      <span className="font-medium">วันที่ตั้งเวลา:</span>
                      <span>{item.date}</span>
                    </div>
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">⏱</span>
                      <span className="font-medium">ระยะเวลาแสดงผล:</span>
                      <span>{item.duration}</span>
                    </div>
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">💵</span>
                      <span className="font-medium">ราคา:</span>
                      <span>{item.price}</span>
                    </div>
                  </div>
                  <button
                    className="self-end bg-red-500 hover:bg-red-600 text-white px-5 py-1.5 rounded font-semibold shadow-sm transition mt-2"
                    onClick={() => handleRemove(item.id)}
                  >
                    REMOVE
                  </button>
                </div>
              ))
            )}
          </div>
          {/* กล่องฝั่งขวา (รูปภาพ) */}
          <div className="flex-1 bg-white rounded-2xl shadow-xl border border-blue-200 flex flex-col min-w-[320px]">
            <div className="text-xl font-bold text-blue-900 text-center py-4 border-b border-blue-100 rounded-t-2xl">
              รายละเอียดการตั้งค่า <span className="text-indigo-600">รูปภาพ</span>
            </div>
            {imageHistory.length === 0 ? (
              <div className="text-center text-gray-400 py-12 text-lg">ไม่มีประวัติการตั้งค่ารูปภาพ</div>
            ) : (
              imageHistory.map((item, idx) => (
                <div
                  key={item.id}
                  className={`flex flex-col gap-2 px-8 py-6 ${
                    idx !== imageHistory.length - 1 ? "border-b border-blue-100" : ""
                  }`}
                >
                  <div className="flex flex-col gap-2">
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">🕒</span>
                      <span className="font-medium">วันที่ตั้งเวลา:</span>
                      <span>{item.date}</span>
                    </div>
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">⏱</span>
                      <span className="font-medium">ระยะเวลาแสดงผล:</span>
                      <span>{item.duration}</span>
                    </div>
                    <div className="text-gray-700 flex items-center gap-2">
                      <span className="text-lg">💵</span>
                      <span className="font-medium">ราคา:</span>
                      <span>{item.price}</span>
                    </div>
                  </div>
                  <button
                    className="self-end bg-red-500 hover:bg-red-600 text-white px-5 py-1.5 rounded font-semibold shadow-sm transition mt-2"
                    onClick={() => handleRemove(item.id)}
                  >
                    REMOVE
                  </button>
                </div>
              ))
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default TimeHistory;
