import React, { useEffect, useState } from "react";

function TimeHistory() {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("timeHistory") || "[]");
    setHistory(data);
  }, []);

  const handleRemove = (id) => {
    const newHistory = history.filter((item) => item.id !== id);
    setHistory(newHistory);
    localStorage.setItem("timeHistory", JSON.stringify(newHistory));
  };

  return (
    <div className="min-h-screen bg-blue-50 flex flex-col items-center">
      {/* Header */}
      <header
        className="main-header"
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          maxWidth: "100vw",
          zIndex: 100,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: "1.2rem 2.5rem",
          boxSizing: "border-box",
          background: "#fff",
          boxShadow: "0 2px 12px rgba(30, 41, 59, 0.08)",
        }}
      >
        <div
          style={{
            fontSize: "2.5rem",
            color: "#1a237e",
            fontWeight: "bold",
            textAlign: "center",
            flex: 1,
            letterSpacing: "1px",
          }}
        >
          ‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥‡∏Å‡∏≤‡∏£‡∏ï‡∏±‡πâ‡∏á‡πÄ‡∏ß‡∏•‡∏≤
        </div>
      </header>

      {/* Main content */}
      <main className="w-full flex justify-center px-4" style={{ marginTop: "110px" }}>
        <div className="w-full max-w-6xl grid grid-cols-1 md:grid-cols-2 gap-6 pb-10">
          {history.length === 0 ? (
            <div className="col-span-2 text-center text-gray-400 text-lg py-12">
              ‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥‡∏Å‡∏≤‡∏£‡∏ï‡∏±‡πâ‡∏á‡∏Ñ‡πà‡∏≤
            </div>
          ) : (
            history.map((item) => (
              <div
                key={item.id}
                className="bg-white p-6 rounded-xl shadow-sm border border-blue-100 flex flex-col justify-between"
              >
                <div className="flex justify-between items-center text-sm text-gray-700 mb-4">
                  <div className="flex items-center gap-2">
                    <span className="text-black">üïí</span>
                    <span>{item.date}</span>
                  </div>
                  <div>{item.duration}</div>
                </div>
                <div className="text-xs text-indigo-700 mb-1">‡∏£‡∏≤‡∏Ñ‡∏≤</div>
                <div className="text-sm font-semibold text-gray-900">{item.price}</div>
                <button
                  className="mt-4 bg-red-500 hover:bg-red-600 text-white text-sm px-4 py-1 rounded-full self-end"
                  onClick={() => handleRemove(item.id)}
                >
                  REMOVE
                </button>
              </div>
            ))
          )}
        </div>
      </main>
    </div>
  );
}

export default TimeHistory;
