import React, { useEffect, useState } from "react";

function CheckHistory() {
  const [history, setHistory] = useState([]);
  const [selected, setSelected] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [editMode, setEditMode] = useState(false);

  useEffect(() => {
    fetchHistory();
  }, []);

  const fetchHistory = () => {
    fetch("http://localhost:5001/api/check-history")
      .then((res) => res.json())
      .then((data) => setHistory(data));
  };

  const handleDelete = async (id) => {
    if (!window.confirm("ยืนยันการลบรายการนี้?")) return;
    await fetch("http://localhost:5001/api/delete-history", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ id }),
    });
    fetchHistory();
  };

  const handleDeleteAll = async () => {
    if (!window.confirm("ยืนยันการลบประวัติทั้งหมด?")) return;
    await fetch("http://localhost:5001/api/delete-all-history", {
      method: "POST",
    });
    fetchHistory();
  };

  const textHistory = history.filter((item) => item.type === "text");
  const imageHistory = history.filter((item) => item.type === "image");

  const formatDate = (dateString) => {
    if (!dateString) return "-";
    return new Date(dateString).toLocaleString("th-TH", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
    });
  };

  // Modal แสดงรายละเอียด
  const DetailModal = ({ item, onClose }) => (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        width: "100vw",
        height: "100vh",
        background: "rgba(0,0,0,0.3)",
        zIndex: 9999,
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <div
        style={{
          background: "#fff",
          borderRadius: 12,
          padding: 32,
          minWidth: 320,
          maxWidth: 400,
          boxShadow: "0 2px 24px 0 rgba(30,41,59,.18)",
        }}
      >
        <h2
          style={{
            fontSize: 20,
            marginBottom: 16,
            color: "#1e293b",
          }}
        >
          รายละเอียดรายการ
        </h2>
        <div style={{ marginBottom: 8 }}>
          <b>ID:</b> {item.id}
        </div>
        {item.type && (
          <div style={{ marginBottom: 8 }}>
            <b>ประเภท:</b> {item.type}
          </div>
        )}
        {item.text && (
          <div style={{ marginBottom: 8 }}>
            <b>ข้อความ:</b> {item.text}
          </div>
        )}
        {item.filePath && (
          <div style={{ marginBottom: 8 }}>
            <b>รูปภาพ:</b>
            <br />
            <img
              src={`http://localhost:5001${item.filePath}`}
              alt="img"
              style={{
                maxWidth: 180,
                borderRadius: 8,
                marginTop: 4,
              }}
            />
          </div>
        )}
        {item.status && (
          <div style={{ marginBottom: 8 }}>
            <b>สถานะ:</b> {item.status === "approved" ? "อนุมัติ" : "ปฏิเสธ"}
          </div>
        )}
        {item.sender && (
          <div style={{ marginBottom: 8 }}>
            <b>ผู้ส่ง:</b> {item.sender}
          </div>
        )}
        {item.price !== undefined && (
          <div style={{ marginBottom: 8 }}>
            <b>ราคา:</b> {item.price}
          </div>
        )}
        {item.createdAt && (
          <div style={{ marginBottom: 8 }}>
            <b>เวลาสร้าง:</b> {formatDate(item.createdAt)}
          </div>
        )}
        {item.checkedAt && (
          <div style={{ marginBottom: 8 }}>
            <b>เวลาตรวจสอบ:</b> {formatDate(item.checkedAt)}
          </div>
        )}
        <button
          onClick={onClose}
          style={{
            marginTop: 16,
            padding: "0.5rem 1.5rem",
            background: "#64748b",
            color: "#fff",
            border: "none",
            borderRadius: 8,
            cursor: "pointer",
          }}
        >
          ปิด
        </button>
      </div>
    </div>
  );

  return (
    <div
      style={{
        minHeight: "100vh",
        width: "100vw",
        fontFamily: "'Segoe UI', Arial, sans-serif",
        position: "relative",
      }}
    >
      {/* พื้นหลังฟ้าอ่อนให้เต็มจอและอยู่ใต้ header */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          zIndex: 0,
          background: "linear-gradient(120deg, #e0e7ff 0%, #f9fafb 100%)",
        }}
      />
      {/* Header เหมือนหน้า Home */}
      <header
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          maxWidth: "100vw",
          zIndex: 10,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: "1.2rem 2.5rem",
          boxSizing: "border-box",
          background: "#fff",
          boxShadow: "0 2px 12px rgba(30, 41, 59, 0.08)",
        }}
      >
        <div
          style={{
            fontSize: "2.2rem",
            color: "#1a237e",
            fontWeight: "bold",
            textAlign: "left",
            flex: 1,
          }}
        >
          CMS ADMIN
        </div>
        <div
          style={{
            flex: 1,
            textAlign: "center",
            fontSize: "1.25rem",
            color: "#222",
            fontWeight: "500",
            userSelect: "none",
            letterSpacing: "1px",
          }}
        >
          ประวัติการตรวจสอบ
        </div>
        <div style={{ flex: 1 }}></div>
      </header>

      {/* Main content */}
      <main
        style={{
          marginTop: "110px",
          width: "100%",
          minHeight: "calc(100vh - 110px)",
          display: "flex",
          justifyContent: "center",
          alignItems: "flex-start",
          position: "relative",
          zIndex: 1,
        }}
      >
        <div
          className="w-full max-w-5xl flex flex-col md:flex-row gap-8"
          style={{
            width: "100%",
            maxWidth: "1200px",
            display: "flex",
            flexDirection: "column",
            gap: "2rem",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <div style={{ width: "100%", display: "flex", justifyContent: "flex-end", gap: 8 }}>
            <button
              style={{
                background: editMode ? "#64748b" : "#3b82f6",
                color: "#fff",
                border: "none",
                borderRadius: 6,
                padding: "0.4rem 1.2rem",
                fontSize: 15,
                cursor: "pointer",
                marginBottom: 8,
              }}
              onClick={() => setEditMode((v) => !v)}
            >
              {editMode ? "ปิดโหมดแก้ไข" : "แก้ไข"}
            </button>
            <button
              style={{
                background: "#ef4444",
                color: "#fff",
                border: "none",
                borderRadius: 6,
                padding: "0.4rem 1.2rem",
                fontSize: 15,
                cursor: "pointer",
                marginBottom: 8,
              }}
              onClick={handleDeleteAll}
            >
              ลบทั้งหมด
            </button>
          </div>
          {/* กรอบซ้าย (ข้อความ) */}
          <div
            className="flex-1 bg-white rounded-2xl shadow-xl border border-blue-200 flex flex-col min-w-[280px] max-w-xl"
            style={{
              flex: 1,
              background: "#fff",
              borderRadius: "1rem",
              boxShadow: "0 2px 24px 0 rgba(30,41,59,.08)",
              border: "1px solid #90caf9",
              minWidth: 280,
              maxWidth: 500,
              margin: 0,
              width: "100%",
            }}
          >
            <div
              className="text-xl font-bold text-blue-900 text-center py-4 border-b border-blue-100 rounded-t-2xl"
              style={{
                fontSize: "1.25rem",
                fontWeight: "bold",
                color: "#1e293b",
                textAlign: "center",
                padding: "1rem 0",
                borderBottom: "1px solid #bbdefb",
                borderTopLeftRadius: "1rem",
                borderTopRightRadius: "1rem",
              }}
            >
              รายละเอียดการตรวจสอบ{" "}
              <span style={{ color: "#ec4899" }}>ข้อความ</span>
            </div>
            {textHistory.length === 0 ? (
              <div
                className="text-center text-gray-400 py-12 text-lg"
                style={{
                  textAlign: "center",
                  color: "#94a3b8",
                  padding: "3rem 0",
                  fontSize: "1.125rem",
                }}
              >
                ไม่มีประวัติการตรวจสอบข้อความ
              </div>
            ) : (
              textHistory.map((item, idx) => (
                <div
                  key={item.id}
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    gap: "0.5rem",
                    padding: "1.5rem 2rem",
                    borderBottom:
                      idx !== textHistory.length - 1 ? "1px solid #bbdefb" : "none",
                    position: "relative",
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "column",
                      gap: "0.5rem",
                    }}
                  >
                    <div style={{ color: "#1e293b" }}>
                      <b>เวลา:</b> {formatDate(item.checkedAt)}
                    </div>
                    <div style={{ color: "#1e293b" }}>
                      <b>รายละเอียด:</b> {item.text}
                    </div>
                    <div style={{ color: "#1e293b" }}>
                      <b>สถานะ:</b> {item.status === "approved" ? "อนุมัติ" : "ปฏิเสธ"}
                    </div>
                    <button
                      style={{
                        marginTop: 8,
                        alignSelf: "flex-start",
                        padding: "0.25rem 1rem",
                        background: "#3b82f6",
                        color: "#fff",
                        border: "none",
                        borderRadius: 6,
                        cursor: "pointer",
                        fontSize: 14,
                      }}
                      onClick={() => {
                        setSelected(item);
                        setShowModal(true);
                      }}
                    >
                      ตรวจสอบรายละเอียด
                    </button>
                  </div>
                  {editMode && (
                    <button
                      style={{
                        position: "absolute",
                        top: 12,
                        right: 12,
                        background: "#ef4444",
                        color: "#fff",
                        border: "none",
                        borderRadius: 6,
                        padding: "0.2rem 0.8rem",
                        fontSize: 13,
                        cursor: "pointer",
                      }}
                      onClick={() => handleDelete(item.id)}
                    >
                      ลบ
                    </button>
                  )}
                </div>
              ))
            )}
          </div>
          {/* กรอบขวา (รูปภาพ) */}
          <div
            className="flex-1 bg-white rounded-2xl shadow-xl border border-blue-200 flex flex-col min-w-[280px] max-w-xl"
            style={{
              flex: 1,
              background: "#fff",
              borderRadius: "1rem",
              boxShadow: "0 2px 24px 0 rgba(30,41,59,.08)",
              border: "1px solid #90caf9",
              minWidth: 280,
              maxWidth: 500,
              margin: 0,
              width: "100%",
            }}
          >
            <div
              className="text-xl font-bold text-blue-900 text-center py-4 border-b border-blue-100 rounded-t-2xl"
              style={{
                fontSize: "1.25rem",
                fontWeight: "bold",
                color: "#1e293b",
                textAlign: "center",
                padding: "1rem 0",
                borderBottom: "1px solid #bbdefb",
                borderTopLeftRadius: "1rem",
                borderTopRightRadius: "1rem",
              }}
            >
              รายละเอียดการตรวจสอบ{" "}
              <span style={{ color: "#6366f1" }}>รูปภาพ</span>
            </div>
            {imageHistory.length === 0 ? (
              <div
                className="text-center text-gray-400 py-12 text-lg"
                style={{
                  textAlign: "center",
                  color: "#94a3b8",
                  padding: "3rem 0",
                  fontSize: "1.125rem",
                }}
              >
                ไม่มีประวัติการตรวจสอบรูปภาพ
              </div>
            ) : (
              imageHistory.map((item, idx) => (
                <div
                  key={item.id}
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    gap: "0.5rem",
                    padding: "1.5rem 2rem",
                    borderBottom:
                      idx !== imageHistory.length - 1 ? "1px solid #bbdefb" : "none",
                    position: "relative",
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "column",
                      gap: "0.5rem",
                    }}
                  >
                    <div style={{ color: "#1e293b" }}>
                      <b>เวลา:</b> {formatDate(item.checkedAt)}
                    </div>
                    <div style={{ color: "#1e293b" }}>
                      <b>รายละเอียด:</b> {item.text}
                    </div>
                    <div style={{ color: "#1e293b" }}>
                      <b>สถานะ:</b> {item.status === "approved" ? "อนุมัติ" : "ปฏิเสธ"}
                    </div>
                    {item.filePath && (
                      <div>
                        <img
                          src={`http://localhost:5001${item.filePath}`}
                          alt="img"
                          style={{
                            maxWidth: 180,
                            marginTop: 8,
                            borderRadius: 8,
                          }}
                        />
                      </div>
                    )}
                    <button
                      style={{
                        marginTop: 8,
                        alignSelf: "flex-start",
                        padding: "0.25rem 1rem",
                        background: "#3b82f6",
                        color: "#fff",
                        border: "none",
                        borderRadius: 6,
                        cursor: "pointer",
                        fontSize: 14,
                      }}
                      onClick={() => {
                        setSelected(item);
                        setShowModal(true);
                      }}
                    >
                      ตรวจสอบรายละเอียด
                    </button>
                  </div>
                  {editMode && (
                    <button
                      style={{
                        position: "absolute",
                        top: 12,
                        right: 12,
                        background: "#ef4444",
                        color: "#fff",
                        border: "none",
                        borderRadius: 6,
                        padding: "0.2rem 0.8rem",
                        fontSize: 13,
                        cursor: "pointer",
                      }}
                      onClick={() => handleDelete(item.id)}
                    >
                      ลบ
                    </button>
                  )}
                </div>
              ))
            )}
          </div>
        </div>
      </main>
      {showModal && selected && (
        <DetailModal item={selected} onClose={() => setShowModal(false)} />
      )}
    </div>
  );
}

export default CheckHistory;
