import React, { useEffect, useState } from "react";

function CheckHistory() {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5001/api/check-history")
      .then((res) => res.json())
      .then((data) => setHistory(data));
  }, []);

  // แยกข้อมูลตามประเภท mode
  const textHistory = history.filter((item) => item.type === "text");
  const imageHistory = history.filter((item) => item.type === "image");

  // ฟังก์ชันแสดงเวลา
  const formatDate = (dateString) => {
    if (!dateString) return "-";
    return new Date(dateString).toLocaleString("th-TH", {
      day: "2-digit",
      month: "2-digit",
      year: "numeric",
      hour: "2-digit",
      minute: "2-digit",
      second: "2-digit",
    });
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        width: "100vw",
        fontFamily: "'Segoe UI', Arial, sans-serif",
        position: "relative",
      }}
    >
      {/* พื้นหลังฟ้าอ่อนให้เต็มจอและอยู่ใต้ header */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          height: "100vh",
          zIndex: 0,
          background: "linear-gradient(120deg, #e0e7ff 0%, #f9fafb 100%)",
        }}
      />
      {/* Header เหมือนหน้า Home */}
      <header
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          maxWidth: "100vw",
          zIndex: 10,
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: "1.2rem 2.5rem",
          boxSizing: "border-box",
          background: "#fff",
          boxShadow: "0 2px 12px rgba(30, 41, 59, 0.08)",
        }}
      >
        <div
          style={{
            fontSize: "2.2rem",
            color: "#1a237e",
            fontWeight: "bold",
            textAlign: "left",
            flex: 1,
          }}
        >
          CMS ADMIN
        </div>
        <div
          style={{
            flex: 1,
            textAlign: "center",
            fontSize: "1.25rem",
            color: "#222",
            fontWeight: "500",
            userSelect: "none",
            letterSpacing: "1px",
          }}
        >
          ประวัติการตรวจสอบ
        </div>
        <div style={{ flex: 1 }}></div>
      </header>

      {/* Main content */}
      <main
        style={{
          marginTop: "110px",
          width: "100%",
          minHeight: "calc(100vh - 110px)",
          display: "flex",
          justifyContent: "center",
          alignItems: "flex-start",
          position: "relative",
          zIndex: 1,
        }}
      >
        <div
          className="w-full max-w-5xl flex flex-col md:flex-row gap-8"
          style={{
            width: "100%",
            maxWidth: "1200px",
            display: "flex",
            flexDirection: "column",
            gap: "2rem",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          {/* กรอบซ้าย (ข้อความ) */}
          <div
            className="flex-1 bg-white rounded-2xl shadow-xl border border-blue-200 flex flex-col min-w-[280px] max-w-xl"
            style={{
              flex: 1,
              background: "#fff",
              borderRadius: "1rem",
              boxShadow: "0 2px 24px 0 rgba(30,41,59,.08)",
              border: "1px solid #90caf9",
              minWidth: 280,
              maxWidth: 500,
              margin: 0,
              width: "100%",
            }}
          >
            <div
              className="text-xl font-bold text-blue-900 text-center py-4 border-b border-blue-100 rounded-t-2xl"
              style={{
                fontSize: "1.25rem",
                fontWeight: "bold",
                color: "#1e293b",
                textAlign: "center",
                padding: "1rem 0",
                borderBottom: "1px solid #bbdefb",
                borderTopLeftRadius: "1rem",
                borderTopRightRadius: "1rem",
              }}
            >
              รายละเอียดการตรวจสอบ{" "}
              <span style={{ color: "#ec4899" }}>ข้อความ</span>
            </div>
            {textHistory.length === 0 ? (
              <div
                className="text-center text-gray-400 py-12 text-lg"
                style={{
                  textAlign: "center",
                  color: "#94a3b8",
                  padding: "3rem 0",
                  fontSize: "1.125rem",
                }}
              >
                ไม่มีประวัติการตรวจสอบข้อความ
              </div>
            ) : (
              textHistory.map((item, idx) => (
                <div
                  key={item.id}
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    gap: "0.5rem",
                    padding: "1.5rem 2rem",
                    borderBottom:
                      idx !== textHistory.length - 1 ? "1px solid #bbdefb" : "none",
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "column",
                      gap: "0.5rem",
                    }}
                  >
                    <div style={{ color: "#1e293b" }}>
                      <b>เวลา:</b> {formatDate(item.checkedAt)}
                    </div>
                    <div style={{ color: "#1e293b" }}>
                      <b>รายละเอียด:</b> {item.text}
                    </div>
                    <div style={{ color: "#1e293b" }}>
                      <b>สถานะ:</b>{" "}
                      {item.status === "approved" ? "อนุมัติ" : "ปฏิเสธ"}
                    </div>
                  </div>
                </div>
              ))
            )} 
          </div>
          {/* กรอบขวา (รูปภาพ) */}
          <div
            className="flex-1 bg-white rounded-2xl shadow-xl border border-blue-200 flex flex-col min-w-[280px] max-w-xl"
            style={{
              flex: 1,
              background: "#fff",
              borderRadius: "1rem",
              boxShadow: "0 2px 24px 0 rgba(30,41,59,.08)",
              border: "1px solid #90caf9",
              minWidth: 280,
              maxWidth: 500,
              margin: 0,
              width: "100%",
            }}
          >
            <div
              className="text-xl font-bold text-blue-900 text-center py-4 border-b border-blue-100 rounded-t-2xl"
              style={{
                fontSize: "1.25rem",
                fontWeight: "bold",
                color: "#1e293b",
                textAlign: "center",
                padding: "1rem 0",
                borderBottom: "1px solid #bbdefb",
                borderTopLeftRadius: "1rem",
                borderTopRightRadius: "1rem",
              }}
            >
              รายละเอียดการตรวจสอบ{" "}
              <span style={{ color: "#6366f1" }}>รูปภาพ</span>
            </div>
            {imageHistory.length === 0 ? (
              <div
                className="text-center text-gray-400 py-12 text-lg"
                style={{
                  textAlign: "center",
                  color: "#94a3b8",
                  padding: "3rem 0",
                  fontSize: "1.125rem",
                }}
              >
                ไม่มีประวัติการตรวจสอบรูปภาพ
              </div>
            ) : (
              imageHistory.map((item, idx) => (
                <div
                  key={item.id}
                  style={{
                    display: "flex",
                    flexDirection: "column",
                    gap: "0.5rem",
                    padding: "1.5rem 2rem",
                    borderBottom:
                      idx !== imageHistory.length - 1 ? "1px solid #bbdefb" : "none",
                  }}
                >
                  <div
                    style={{
                      display: "flex",
                      flexDirection: "column",
                      gap: "0.5rem",
                    }}
                  >
                    <div style={{ color: "#1e293b" }}>
                      <b>เวลา:</b> {formatDate(item.checkedAt)}
                    </div>
                    <div style={{ color: "#1e293b" }}>
                      <b>รายละเอียด:</b> {item.text}
                    </div>
                    <div style={{ color: "#1e293b" }}>
                      <b>สถานะ:</b>{" "}
                      {item.status === "approved" ? "อนุมัติ" : "ปฏิเสธ"}
                    </div>
                    {item.filePath && (
                      <div>
                        <img
                          src={`http://localhost:5001${item.filePath}`}
                          alt="img"
                          style={{
                            maxWidth: 180,
                            marginTop: 8,
                            borderRadius: 8,
                          }}
                        />
                      </div>
                    )}
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </main>
    </div>
  );
}

export default CheckHistory;
