import React, { useEffect, useState } from "react";

function TimeHistory() {
  const [history, setHistory] = useState([]);

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("timeHistory") || "[]");
    setHistory(data);
  }, []);

