import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./home.css";

function Home() {
  const [systemOn, setSystemOn] = useState(true);
  const [mode, setMode] = useState("image");
  const [second, setSecond] = useState("");
  const [minute, setMinute] = useState("");
  const [hour, setHour] = useState("");
  const [price, setPrice] = useState("");
  const navigate = useNavigate();

  // ตรวจสอบว่ากรอกเวลาอย่างน้อย 1 ช่อง และกรอกราคา
  const isTimeSet = !!second || !!minute || !!hour;
  const isPriceSet = !!price;

  const handleSave = () => {
    if (!isTimeSet || !isPriceSet) return;
    const newRecord = {
      date: new Date().toLocaleDateString("th-TH", {
        day: "2-digit",
        month: "short",
        year: "numeric",
      }),
      duration: `${hour ? hour + " ชั่วโมง " : ""}${
        minute ? minute + " นาที " : ""
      }${second ? second + " วินาที" : ""}`,
      price: price + " ฿",
      id: Date.now(),
      mode: mode,
    };
    const history = JSON.parse(localStorage.getItem("timeHistory") || "[]");
    history.unshift(newRecord);
    localStorage.setItem("timeHistory", JSON.stringify(history));
    alert("บันทึกสำเร็จ...");
  };

  const handleToggleSystem = () => {
    setSystemOn((prev) => !prev);
    // ส่งสถานะไป backend เพื่อ sync กับ user
  };

  return (
    <div className="home-container">
      {/* Header bar */}
      <header
        className="main-header"
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: "100vw",
          maxWidth: "100vw",
          zIndex: 100,
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          padding: "1.2rem 2.5rem",
          boxSizing: "border-box",
          background: "#fff",
          boxShadow: "0 2px 12px rgba(30, 41, 59, 0.08)",
        }}
      >
        {/* ซ้าย */}
        <div
          className="main-header-title"
          style={{
            fontSize: "2.2rem",
            color: "#1a237e",
            fontWeight: "bold",
            textAlign: "left",
            flex: 1,
          }}
        >
          CMS ADMIN
        </div>
        {/* กลาง */}
        <div
          style={{
            flex: 1,
            textAlign: "center",
            fontSize: "1.25rem",
            color: "#222",
            fontWeight: "500",
            cursor: "pointer",
            userSelect: "none",
          }}
          onClick={() => navigate("/time-history")}
        >
          ประวัติการตั้งเวลา
        </div>
        {/* ขวา */}
        <div
          className="status-row-header"
          style={{
            display: "flex",
            alignItems: "center",
            flex: 1,
            justifyContent: "flex-end",
          }}
        >
          <span className="status-label" style={{ marginRight: 10 }}>
            สถานะระบบ:
          </span>
          <div
            className={`switch-track ${systemOn ? "switch-on" : "switch-off"}`}
            onClick={handleToggleSystem}
            style={{ cursor: "pointer" }}
            title={systemOn ? "ปิดระบบ" : "เปิดระบบ"}
          >
            <div className="switch-thumb"></div>
          </div>
        </div>
      </header>

      {/* Main content */}
      <main className="main-content" style={{ marginTop: "90px" }}>
        <section className="mode-row">
          <span>ตั้งค่าสำหรับ:</span>
          <button
            className={`mode-btn${mode === "image" ? " active" : ""}`}
            onClick={() => setMode("image")}
            disabled={!systemOn}
          >
            รูปภาพ
          </button>
          <button
            className={`mode-btn${mode === "text" ? " active" : ""}`}
            onClick={() => setMode("text")}
            disabled={!systemOn}
          >
            ข้อความ
          </button>
        </section>
        {!systemOn && (
          <div className="system-off-msg" style={{ marginTop: 10 }}>
            * ระบบถูกปิด ฝั่ง user จะไม่สามารถเลือกส่งภาพหรือข้อความได้
          </div>
        )}

        <section className="setting-row">
          <div className="time-row">
            <label>ตั้งเวลา :</label>
            <div className="time-inputs">
              <input
                type="number"
                min="1"
                max="59"
                placeholder="วินาที"
                value={second}
                onChange={(e) => setSecond(e.target.value)}
                disabled={!systemOn}
              />
              <input
                type="number"
                min="1"
                max="59"
                placeholder="นาที"
                value={minute}
                onChange={(e) => setMinute(e.target.value)}
                disabled={!systemOn}
              />
              <input
                type="number"
                min="1"
                max="24"
                placeholder="ชั่วโมง"
                value={hour}
                onChange={(e) => setHour(e.target.value)}
                disabled={!systemOn}
              />
            </div>
          </div>

          <div className="price-row">
            <label>ตั้งราคา (บาท):</label>
            <input
              type="number"
              min="1"
              placeholder="ราคา"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              disabled={!systemOn}
            />
          </div>

          <button
            className="save-btn"
            onClick={handleSave}
            disabled={!systemOn || !isTimeSet || !isPriceSet}
          >
            บันทึก
          </button>
        </section>
      </main>
    </div>
  );
}

export default Home;
